import React from "react";
import './AboutMeCard.css';
import Footer from '../footer/Footer';

function AboutMeCard() {
    // Create the resume container
    const resumeContainer = document.createElement("div");
    resumeContainer.classList.add("container");

    const resumeData = {
        name: "Daniel Smith",
        title: "Site Reliability Engineer",
        contact: {
            email: "daniel.smith@example.com",
            phone: "(123) 456-7890",
            location: "Anytown, USA",
            website: "www.daniel-portfolio.com"
        },
        experience: [
            {
                role: "Site Reliability Engineer",
                company: "Black Knight Inc. (ICE)",
                date: "June 2022 - Present",
                duties: [
                    "Managed site reliability and systems performance for large-scale services.",
                    "Automated infrastructure tasks, reducing manual interventions by 30%.",
                    "Monitored system reliability using CI/CD pipelines in Azure DevOps."
                ]
            },
            {
                role: "Systems Analyst Intern",
                company: "Techmind Innovations",
                date: "January 2022 - May 2022",
                duties: [
                    "Optimized system performance for internal applications.",
                    "Collaborated with teams to resolve issues across environments.",
                    "Developed automation tools to streamline reporting and alerting."
                ]
            }
        ],
        skills: [
            "Cloud Services: AWS, Azure",
            "CI/CD: Azure DevOps, Jenkins",
            "Automation: Terraform, Ansible",
            "Monitoring: Prometheus, Grafana, Splunk",
            "Scripting: Python, Bash, PowerShell"
        ],
        education: {
            degree: "Bachelor of Science in Computer Science",
            institution: "University of Example",
            year: "2020"
        }
    };

    // Header
    const header = document.createElement("header");
    const nameHeading = document.createElement("h1");
    nameHeading.textContent = resumeData.name;
    const titleParagraph = document.createElement("p");
    titleParagraph.textContent = resumeData.title;

    header.appendChild(nameHeading);
    header.appendChild(titleParagraph);
    resumeContainer.appendChild(header);

    // Contact Info
    const contactSection = document.createElement("section");
    contactSection.classList.add("contact-info");
    const contactHeading = document.createElement("h2");
    contactHeading.textContent = "Contact Information";
    const contactList = document.createElement("ul");
    for (const [key, value] of Object.entries(resumeData.contact)) {
        const listItem = document.createElement("li");
        listItem.innerHTML = `${key.charAt(0).toUpperCase() + key.slice(1)}: ${value}`;
        contactList.appendChild(listItem);
    }
    contactSection.appendChild(contactHeading);
    contactSection.appendChild(contactList);
    resumeContainer.appendChild(contactSection);

    // Experience
    const experienceSection = document.createElement("section");
    experienceSection.classList.add("experience");
    const experienceHeading = document.createElement("h2");
    experienceHeading.textContent = "Professional Experience";
    experienceSection.appendChild(experienceHeading);

    resumeData.experience.forEach(job => {
        const jobDiv = document.createElement("div");
        jobDiv.classList.add("job");

        const jobTitle = document.createElement("h3");
        jobTitle.textContent = `${job.role} - ${job.company}`;
        const jobDate = document.createElement("p");
        jobDate.textContent = job.date;

        const dutiesList = document.createElement("ul");
        job.duties.forEach(duty => {
            const dutyItem = document.createElement("li");
            dutyItem.textContent = duty;
            dutiesList.appendChild(dutyItem);
        });

        jobDiv.appendChild(jobTitle);
        jobDiv.appendChild(jobDate);
        jobDiv.appendChild(dutiesList);
        experienceSection.appendChild(jobDiv);
    });
    resumeContainer.appendChild(experienceSection);

    // Skills
    const skillsSection = document.createElement("section");
    skillsSection.classList.add("skills");
    const skillsHeading = document.createElement("h2");
    skillsHeading.textContent = "Skills";
    const skillsList = document.createElement("ul");
    resumeData.skills.forEach(skill => {
        const skillItem = document.createElement("li");
        skillItem.textContent = skill;
        skillsList.appendChild(skillItem);
    });
    skillsSection.appendChild(skillsHeading);
    skillsSection.appendChild(skillsList);
    resumeContainer.appendChild(skillsSection);

    // Education
    const educationSection = document.createElement("section");
    educationSection.classList.add("education");
    const educationHeading = document.createElement("h2");
    educationHeading.textContent = "Education";
    const educationParagraph = document.createElement("p");
    educationParagraph.textContent = `${resumeData.education.degree}, ${resumeData.education.institution} (${resumeData.education.year})`;
    educationSection.appendChild(educationHeading);
    educationSection.appendChild(educationParagraph);
    resumeContainer.appendChild(educationSection);

    // Return the resume container element
    return resumeContainer;
}

// To inject the created resume into the DOM, you can call the function and append the result to a specific location in your webpage.
const appContainer = document.getElementById("app"); // Assuming there is a div with id "app"
appContainer.appendChild(AboutMeCard());



